import React, { useEffect, useRef } from 'react';

class Canvas {
  constructor(canvas) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d');
  }

  draw() {
    // Draw the red banner
    this.ctx.fillStyle = '#FF0000';
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height / 2);

    // Draw the text
    this.ctx.fillStyle = '#FFFFFF';
    this.ctx.font = '30px Arial';
    this.ctx.fillText('1 & 2 BHK Luxury Apartments at just Rs.34.97 Lakhs', 10, 50);

    // Draw the "Shop Now" button
    this.ctx.fillStyle = '#FFFFFF';
    this.ctx.fillRect(this.canvas.width - 100, 10, 90, 30);
    this.ctx.fillStyle = '#000000';
    this.ctx.fillText('Shop Now', this.canvas.width - 90, 30);

    // Draw the image (assuming the image is loaded)
    const img = new Image();
    img.src = this.templateData.urls.coffeeimg; // replace with your image path
    img.onload = () => {
      this.ctx.drawImage(img, 0, this.canvas.height / 2, this.canvas.width, this.canvas.height / 2);
    };
  }
}

const CanvasEditor = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = new Canvas(canvasRef.current);
    canvas.draw();
  }, []);

  return (
    <canvas
      ref={canvasRef}
      width={1080}
      height={1080}
      style={{ height: 400, width: 400 }}
    />
  );
};

export default CanvasEditor;
