import React, { useRef, useState, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import './styles.css'

const CanvasEditor = ({ templateData }) => {
  const canvasRef = useRef(null);
  const [caption, setCaption] = useState(templateData.caption.text);
  const [callToAction, setCallToAction] = useState(templateData.cta.text);
  const [backgroundColor, setBackgroundColor] = useState('#0369A1'); // Default background color
  const [showColorPicker, setShowColorPicker] = useState(false);
  const MAX_PICKED_COLORS = 5;
  const [pickedColors, setPickedColors] = useState(getRandomColors()); 
  const [colors, setColors] = useState(['#ff0000', '#00ff00', '#0000ff', '#ffff00', '#ff00ff']);


  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw background color
    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Load design pattern
    const designPattern = new Image();
    designPattern.src = (templateData.urls.design_pattern);
    designPattern.onload = () => {
      ctx.drawImage(designPattern, 0, 0, canvas.width, canvas.height);

      // Apply mask
      const mask = new Image();
      mask.src = addRandomQueryParam(templateData.urls.mask);
      mask.onload = () => {
        ctx.drawImage(mask, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

        // Apply mask stroke
        // const maskStroke = new Image();
        // maskStroke.src = (templateData.urls.stroke);
        // maskStroke.onload = () => {
        //   ctx.drawImage(maskStroke, templateData.image_mask.x+1, templateData.image_mask.y+1, templateData.image_mask.width, templateData.image_mask.height);
        ctx.save(); // Save the current state of the context
        ctx.strokeStyle = 'white'; // Set stroke color to white
        ctx.lineWidth = 10; // Set stroke width (adjust as needed)
        ctx.strokeRect(templateData.image_mask.x - 5, templateData.image_mask.y - 5, templateData.image_mask.width + 10, templateData.image_mask.height + 10); // Draw a stroke rectangle around the mask
        ctx.restore();

          // Draw caption
          drawText(ctx, caption, templateData.caption);

          // Draw call to action
          drawCTA(ctx, callToAction, templateData.cta);
        };
      };
    // };
  }, [backgroundColor,caption,callToAction,templateData]);

  const handleCaptionChange = (event) => {
    setCaption(event.target.value);
  };

  const handleCallToActionChange = (event) => {
    setCallToAction(event.target.value);
  };

  const handleBackgroundColorChange = (color) => {
    setBackgroundColor(color.hex);
  };

  const handleColorPickerToggle = () => {
    setShowColorPicker(!showColorPicker);
  };

  const handleColorChange = (event) => {
    let newColors = [event.target.value, ...colors];
    if (newColors.length > 5) {
      newColors.pop();
    }
    setColors(newColors);
  };

  const addRandomQueryParam = (url) => {
    const randomQueryParam = Math.floor(Math.random() * 100000); // Generate a random number
    return `${url}?random=${randomQueryParam}`;
  };

  // Function to draw text with line breaks
const drawText = (ctx, text, data) => {
  ctx.fillStyle = data.text_color;
  ctx.font = `${data.font_size}px Arial`;
  ctx.textAlign = data.alignment;

  const lines = breakTextIntoLines(text, data.max_characters_per_line); // Break text into lines with max characters per line specified in data
  let offsetY = data.position.y;
  lines.forEach((line) => {
    ctx.fillText(line, data.position.x, offsetY);
    offsetY += data.font_size + 5; // Adjust for line spacing
  });
};

const drawCTA = (ctx, text, data) => {
  // Function to wrap text to fit within a given width
  const wrapText = (text, wrapLength) => {
    const words = text.split(' ');
    const lines = [];
    let currentLine = '';

    words.forEach(word => {
      const testLine = currentLine + word + ' ';
      const testWidth = ctx.measureText(testLine).width;
      if (testWidth > wrapLength && currentLine !== '') {
        lines.push(currentLine);
        currentLine = word + ' ';
      } else {
        currentLine = testLine;
      }
    });

    lines.push(currentLine);
    return lines;
  };

  const ctaX = data.position.x;
  const ctaY = data.position.y;
  const ctaWidth = data.width || 200; // Default width for the CTA button
  const ctaHeight = data.height || 80; // Default height for the CTA button
  const cornerRadius = data.cornerRadius || 10; // Default corner radius for rounded corners
  const textColor = data.text_color || 'white'; // Default text color
  const bgColor = data.backgroundColor || 'black'; // Default background color
  const padding = data.padding || 16; // Default padding around the text
  const wrapLength = data.wrapLength || 20; // Default wrap length for text

  // Draw rounded rectangle background for CTA button
  ctx.beginPath();
  ctx.moveTo(ctaX + cornerRadius, ctaY);
  ctx.arcTo(ctaX + ctaWidth, ctaY, ctaX + ctaWidth, ctaY + ctaHeight, cornerRadius);
  ctx.arcTo(ctaX + ctaWidth, ctaY + ctaHeight, ctaX, ctaY + ctaHeight, cornerRadius);
  ctx.arcTo(ctaX, ctaY + ctaHeight, ctaX, ctaY, cornerRadius);
  ctx.arcTo(ctaX, ctaY, ctaX + ctaWidth, ctaY, cornerRadius);
  ctx.closePath();

  ctx.fillStyle = bgColor;
  ctx.fill();

  // Draw text on the CTA button
  ctx.fillStyle = textColor;
  ctx.font = `${data.font_size || 30}px Arial`; // Default font size for the text
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';

  // Wrap text if it exceeds wrapLength
  const wrappedText = wrapText(text, wrapLength);
  const lineHeight = ctx.measureText('M').width; // Approximate line height

  // Calculate vertical position to vertically center the text
  const textY = ctaY + ctaHeight / 2 - (lineHeight * wrappedText.length) / 2;

  // Draw wrapped text with padding
  wrappedText.forEach((line, index) => {
    ctx.fillText(line, ctaX + ctaWidth / 2, textY + index * lineHeight + padding / 2);
  });
};

function getRandomColors() {
  const colors = [];
  for (let i = 0; i < MAX_PICKED_COLORS; i++) {
    colors.push('#' + Math.floor(Math.random() * 16777215).toString(16)); // Generate random hex color
  }
  return colors;
}

  const breakTextIntoLines = (text, maxCharactersPerLine) => {
    const lines = [];
    let currentLine = '';
  
    for (let i = 0; i < text.length; i++) {
      currentLine += text[i];
      if (currentLine.length === maxCharactersPerLine || i === text.length - 1) {
        lines.push(currentLine);
        currentLine = '';
      }
    }
    return lines;
  };

  return (
    <div className="flex flex-col items-center">
      <canvas
        ref={canvasRef}
        width={1080}
        height={1080}
        className="border border-gray-400"
        style={{ width: '400px', height: '400px' }}
      />
      <div className="mt-4 flex flex-col items-center">
        <input
          type="text"
          value={caption}
          onChange={handleCaptionChange}
          placeholder="Enter Caption"
          className="border border-gray-400 p-2 mb-2"
        />
        <input
          type="text"
          value={callToAction}
          onChange={handleCallToActionChange}
          placeholder="Enter CTA Text"
          className="border border-gray-400 p-2 mb-2"
        />
        <div className="color-picker">
        <div className="color-list">
          {/* Display last five picked colors */}
          {pickedColors.slice(0, 5).map((color, index) => (
            <button
              key={index}
              className="color-box"
              style={{ backgroundColor: color }}
              onClick={() => setBackgroundColor(color)}
            />
          ))}
          {/* Button with + icon */}
          <button className="color-picker-btn" onClick={handleColorPickerToggle}>
            +
          </button>
        </div>
        {showColorPicker && (
          <ChromePicker color={backgroundColor} onChange={handleBackgroundColorChange} />
        )}
      </div>
    </div>
  </div>
);
  
};

export default CanvasEditor;
