import React, { useRef, useState, useEffect } from 'react';
import {ChromePicker} from 'react-color';
import './styles.css';

const CanvasEditor = ({ templateData }) => {
  const canvasRef = useRef(null);
  const [caption, setCaption] = useState(templateData.caption.text);
  const [callToAction, setCallToAction] = useState(templateData.cta.text);
  const [backgroundColor, setBackgroundColor] = useState('#0369A1'); // Default background color

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Draw background color
    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Load design pattern
    const designPattern = new Image();
    designPattern.onload = () => {
      ctx.drawImage(designPattern, 0, 0, canvas.width, canvas.height);

      // Apply mask
      const userImage = new Image();
      userImage.src = templateData.urls.coffeeimg;
      userImage.onload = () => {
        // Resize user image to fit the mask size
        ctx.drawImage(userImage, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

        // Draw the image inside the clipping path
        ctx.drawImage(mask, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

        // Load mask stroke
        const maskStroke = new Image();
        maskStroke.src = addRandomQueryParam(templateData.urls.stroke);
        maskStroke.onload = () => {
          // Draw the mask stroke on top of the mask
          ctx.drawImage(maskStroke, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

          // Draw caption
          ctx.fillStyle = templateData.caption.text_color;
          ctx.font = `${templateData.caption.font_size}px Arial`;
          ctx.textAlign = templateData.caption.alignment;
          const lines = getLines(ctx, caption, templateData.caption.max_characters_per_line);
          lines.forEach((line, index) => {
            ctx.fillText(line, templateData.caption.position.x, templateData.caption.position.y + index * (templateData.caption.font_size + 5));
          });

          // Draw call to action
          const ctaFontSize = templateData.cta.font_size || 30; // Default font size if not provided
          ctx.fillStyle = templateData.cta.text_color;
          ctx.fillRect(templateData.cta.position.x, templateData.cta.position.y, 100, 40);
          ctx.fillStyle = templateData.cta.background_color;
          ctx.textAlign = 'center';
          ctx.textBaseline = 'middle';
          ctx.font = `${ctaFontSize}px Arial`;
          const ctaX = templateData.cta.position.x + 50; // Center of the rectangle
          const ctaY = templateData.cta.position.y + 20; // Middle of the rectangle
          ctx.fillText(callToAction, ctaX, ctaY);
        };
      };
    };

    designPattern.src = addRandomQueryParam(templateData.urls.design_pattern);
  }, [caption, callToAction, backgroundColor, templateData]);

  const handleCaptionChange = (event) => {
    setCaption(event.target.value);
  };

  const handleCallToActionChange = (event) => {
    setCallToAction(event.target.value);
  };

  const handleBackgroundColorChange = (color) => {
    setBackgroundColor(color.hex);
  };

  const addRandomQueryParam = (url) => {
    const randomQueryParam = Math.floor(Math.random() * 100000); // Generate a random number
    return `${url}?random=${randomQueryParam}`;
  };

  const getLines = (ctx, text, maxWidth) => {
    const words = text.split(' ');
    const lines = [];
    let currentLine = words[0];

    for (let i = 1; i < words.length; i++) {
      const word = words[i];
      const width = ctx.measureText(`${currentLine} ${word}`).width;
      if (width < maxWidth) {
        currentLine += ` ${word}`;
      } else {
        lines.push(currentLine);
        currentLine = word;
      }
    }
    lines.push(currentLine);
    return lines;
  };

  return (
    <div className="canvas-container">
      <canvas
        ref={canvasRef}
        width={1024}
        height={768}
        className="border border-gray-400"
      />
      <div className="mt-4">
        <input
          type="text"
          value={caption}
          onChange={handleCaptionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <input
          type="text"
          value={callToAction}
          onChange={handleCallToActionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <div className="color-picker">
          <ChromePicker color={backgroundColor} onChangeComplete={handleBackgroundColorChange} />
        </div>
      </div>
    </div>
  );
};

export default CanvasEditor;
