import React, { useRef, useState, useEffect } from 'react';
import "./styles.css";

const CanvasEditor = ({ templateData }) => {
  const canvasRef = useRef(null);
  const [caption, setCaption] = useState(templateData.caption.text);
  const [callToAction, setCallToAction] = useState(templateData.cta.text);
  const [backgroundColor, setBackgroundColor] = useState(templateData.cta.background_color);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const image = new Image();
    image.onload = () => {
      // Draw background image
      ctx.drawImage(image, 0, 0, canvas.width, canvas.height);

      // Apply mask
      const mask = new Image();
      mask.src = templateData.urls.mask;
      mask.onload = () => {
        ctx.globalCompositeOperation = 'source-atop';
        ctx.drawImage(mask, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

        // Draw image within mask
        const imageToMask = new Image();
        imageToMask.src = 'my-ap\src\Coffee.jpg';
        imageToMask.onload = () => {
          ctx.drawImage(imageToMask, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);
        };
      };

      // Draw caption
      ctx.fillStyle = templateData.caption.text_color;
      ctx.font = `${templateData.caption.font_size}px Arial`;
      ctx.textAlign = templateData.caption.alignment;
      const lines = getLines(ctx, caption, templateData.caption.max_characters_per_line);
      lines.forEach((line, index) => {
        ctx.fillText(line, templateData.caption.position.x, templateData.caption.position.y + index * (templateData.caption.font_size + 5));
      });

      // Draw call to action
      ctx.fillStyle = templateData.cta.text_color;
      ctx.fillRect(templateData.cta.position.x, templateData.cta.position.y, 100, 40);
      ctx.fillStyle = templateData.cta.background_color;
      ctx.fillText(callToAction, templateData.cta.position.x + 10, templateData.cta.position.y + 30);
    };

    image.src = templateData.urls.design_pattern;
  }, [caption, callToAction, backgroundColor, templateData]);

  const handleCaptionChange = (event) => {
    setCaption(event.target.value);
  };

  const handleCallToActionChange = (event) => {
    setCallToAction(event.target.value);
  };

  const handleBackgroundColorChange = (event) => {
    setBackgroundColor(event.target.value);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <canvas
        ref={canvasRef}
        width={1024}
        height={768}
        className="border border-gray-400"
      />
      <div className="mt-4">
        <input
          type="text"
          value={caption}
          onChange={handleCaptionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <input
          type="text"
          value={callToAction}
          onChange={handleCallToActionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <input
          type="color"
          value={backgroundColor}
          onChange={handleBackgroundColorChange}
          className="border border-gray-400 p-2"
        />
      </div>
    </div>
  );
};

const getLines = (ctx, text, maxWidth) => {
  const words = text.split(' ');
  const lines = [];
  let currentLine = words[0];

  for (let i = 1; i < words.length; i++) {
    const word = words[i];
    const width = ctx.measureText(`${currentLine} ${word}`).width;
    if (width < maxWidth) {
      currentLine += ` ${word}`;
    } else {
      lines.push(currentLine);
      currentLine = word;
    }
  }
  lines.push(currentLine);
  return lines;
};

export default CanvasEditor;
