import React, { useEffect, useRef, useState } from 'react';

class Canvas {
  constructor(canvas, templateData) {
    this.canvas = canvas;
    this.ctx = canvas.getContext('2d');
    this.templateData = templateData;
  }

  draw() {
    const image = this.templateData.urls.coffeeimg;
    image.onload = () => {
      this.ctx.drawImage(image, 0, 0, this.canvas.width, this.canvas.height);
      this.drawMask();
      this.drawCaption();
      this.drawCTA();
    };
    image.src = this.templateData.urls.design_pattern;
  }

  drawMask() {
    const mask = new Image();
    mask.onload = () => {
      this.ctx.globalCompositeOperation = 'source-atop';
      this.ctx.drawImage(mask, this.templateData.image_mask.x, this.templateData.image_mask.y, this.templateData.image_mask.width, this.templateData.image_mask.height);
    };
    mask.src = this.templateData.urls.mask;
  }

  drawCaption() {
    this.ctx.fillStyle = this.templateData.caption.text_color;
    this.ctx.font = `${this.templateData.caption.font_size}px Arial`;
    this.ctx.textAlign = this.templateData.caption.alignment;
    const lines = this.getLines(this.templateData.caption.text, this.templateData.caption.max_characters_per_line);
    lines.forEach((line, index) => {
      this.ctx.fillText(line, this.templateData.caption.position.x, this.templateData.caption.position.y + index * (this.templateData.caption.font_size + 5));
    });
  }

  drawCTA() {
    this.ctx.fillStyle = this.templateData.cta.text_color;
    this.ctx.fillRect(this.templateData.cta.position.x, this.templateData.cta.position.y, 100, 40);
    this.ctx.fillStyle = this.templateData.cta.background_color;
    this.ctx.fillText(this.templateData.cta.text, this.templateData.cta.position.x + 10, this.templateData.cta.position.y + 30);
  }

  getLines(text, maxWidth) {
    const words = text.split(' ');
    const lines = [];
    let currentLine = words[0];

    for (let i = 1; i < words.length; i++) {
      const word = words[i];
      const width = this.ctx.measureText(`${currentLine} ${word}`).width;
      if (width < maxWidth) {
        currentLine += ` ${word}`;
      } else {
        lines.push(currentLine);
        currentLine = word;
      }
    }
    lines.push(currentLine);
    return lines;
  }
}

const CanvasEditor = ({ templateData }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = new Canvas(canvasRef.current, templateData);
    canvas.draw();
  }, [templateData]);

  return (
    <canvas
      ref={canvasRef}
      width={1080}
      height={1080}
      style={{ height: 400, width: 400 }}
    />
  );
};

export default CanvasEditor;
