import React, { useRef, useState, useEffect } from 'react';
import { ChromePicker } from 'react-color';
import './styles.css';

const CanvasEditor = ({ templateData }) => {
  const canvasRef = useRef(null);
  const [caption, setCaption] = useState(templateData.caption.text);
  const [callToAction, setCallToAction] = useState(templateData.cta.text);
  const [backgroundColor, setBackgroundColor] = useState('#0369A1'); // Default background color

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Calculate the scale factor for the canvas
    const scaleFactor = 400 / 1080;

    // Apply the scale to the canvas
    ctx.scale(scaleFactor, scaleFactor);

    // Draw background color
    ctx.fillStyle = backgroundColor;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Load design pattern
    const designPattern = new Image();
    designPattern.onload = () => {
      ctx.drawImage(designPattern, 0, 0, canvas.width, canvas.height);

      // Apply mask
      const userImage = new Image();
      userImage.src = templateData.urls.coffeeimg;
      userImage.onload = () => {
        // Resize user image to fit the mask size
        ctx.drawImage(userImage, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

        // Load mask stroke
        const maskStroke = new Image();
        maskStroke.src = addRandomQueryParam(templateData.urls.stroke);
        maskStroke.onload = () => {
          // Draw the mask stroke on top of the mask
          ctx.drawImage(maskStroke, templateData.image_mask.x, templateData.image_mask.y, templateData.image_mask.width, templateData.image_mask.height);

          // Draw caption
          drawCaption(ctx, caption, templateData.caption);

          // Draw call to action
          drawCallToAction(ctx, callToAction, templateData.cta);
        };
      };
    };

    designPattern.src = addRandomQueryParam(templateData.urls.design_pattern);
  }, [caption, callToAction, backgroundColor, templateData]);

  const handleCaptionChange = (event) => {
    setCaption(event.target.value);
  };

  const handleCallToActionChange = (event) => {
    setCallToAction(event.target.value);
  };

  const handleBackgroundColorChange = (color) => {
    setBackgroundColor(color.hex);
  };

  const addRandomQueryParam = (url) => {
    const randomQueryParam = Math.floor(Math.random() * 100000); // Generate a random number
    return `${url}?random=${randomQueryParam}`;
  };

  const drawCaption = (ctx, text, captionData) => {
    ctx.fillStyle = captionData.text_color;
    ctx.font = `${captionData.font_size}px Arial`;
    ctx.textAlign = captionData.alignment;
    
    const lines = breakTextIntoLines(ctx, text, captionData.max_characters_per_line);
    let offsetY = captionData.position.y;
    lines.forEach((line) => {
      ctx.fillText(line, captionData.position.x, offsetY);
      offsetY += captionData.font_size + 5; // Adjust for line spacing
    });
  };

  const drawCallToAction = (ctx, text, ctaData) => {
    ctx.fillStyle = ctaData.text_color;
    ctx.fillRect(ctaData.position.x, ctaData.position.y, ctx.measureText(text).width + 48, ctaData.font_size + 24);
    ctx.fillStyle = ctaData.background_color;
    const cornerRadius = 10; // Rounded corner radius
    ctx.lineJoin = 'round';
    ctx.lineWidth = cornerRadius;
    ctx.strokeStyle = ctaData.background_color;
    ctx.strokeRect(ctaData.position.x + cornerRadius / 2, ctaData.position.y + cornerRadius / 2, ctx.measureText(text).width + 47, ctaData.font_size + 23);
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.font = `${ctaData.font_size || 30}px Arial`;
    ctx.fillStyle = ctaData.text_color;
    ctx.fillText(text, ctaData.position.x + (ctx.measureText(text).width + 48) / 2, ctaData.position.y + (ctaData.font_size + 24) / 2);
  };

  const breakTextIntoLines = (ctx, text, maxWidth) => {
    const words = text.split(' ');
    const lines = [];
    let currentLine = '';

    words.forEach((word) => {
      const width = ctx.measureText(`${currentLine} ${word}`).width;
      if (width < maxWidth || currentLine === '') {
        currentLine += (currentLine === '' ? '' : ' ') + word;
      } else {
        lines.push(currentLine);
        currentLine = word;
      }
    });
    lines.push(currentLine); // Push the last line
    return lines;
  };

  return (
    <div className="canvas-container" style={{ width: '400px', height: '400px' }}>
      <canvas
        ref={canvasRef}
        width={1080}
        height={1080}
        className="border border-gray-400"
        style={{ width: '400px', height: '400px' }}
      />
      <div className="mt-4">
        <input
          type="text"
          value={caption}
          onChange={handleCaptionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <input
          type="text"
          value={callToAction}
          onChange={handleCallToActionChange}
          className="border border-gray-400 p-2 mr-2"
        />
        <div className="color-picker">
          <ChromePicker color={backgroundColor} onChangeComplete={handleBackgroundColorChange} />
        </div>
      </div>
    </div>
  );
};

export default CanvasEditor;
